import React, { useState, useEffect } from "react";
import './Blog.css';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Blog() {
    const [latestBlog, setLatestBlog] = useState({});
    const [otherBlogs, setOtherBlogs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3001/api/blogs')
            .then(response => {
                const blogs = response.data;
                const sortedBlogs = blogs.sort((a, b) => b.id - a.id);  // Ordenando os blogs em ordem decrescente pelo ID
                setLatestBlog(sortedBlogs[0]);  // Primeiro blog é o mais recente
                setOtherBlogs(sortedBlogs.slice(1));  // Os outros blogs
            })
            .catch(error => console.error("Erro ao buscar blogs:", error));
    }, []);

    return (
        <>
            <Header />
            <div className="blog-container">
                <a href="/">
                    <img src="https://bfbaby.com.br/up/voltar.png" alt="voltar" className="back" />
                </a>
                <h1 className="title-blog">Blog</h1>
                <div className="blog-content">
                    <div className="images-placeholder-blog" style={{ backgroundImage: `url(${latestBlog.image2 || ''})` }}></div>
                    <div className="text-content">
                        <h2>{latestBlog.title || 'Título do Blog'}</h2>
                        <p>{latestBlog.description || 'Descrição do Blog'}</p>
                        <Link to={`/materia/${latestBlog.slug}`}>
                            <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="leiaMais" />
                        </Link>
                    </div>
                </div>

                <div className="blog-cards">
                    {otherBlogs.map(blog => (
                        <div key={blog.id} className="blog-card">
                            <div className="blog-card-image" style={{ backgroundImage: `url(${blog.image})` }}></div>
                            <div className="blog-card-content">
                                <h2>{blog.title}</h2>
                                <p>{blog.description}</p>
                                <Link to={`/materia/${blog.slug}`}>
                                    <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="blog-read-more" />
                                </Link>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <Footer />
        </>
    );
}

export default Blog;
