import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './faqSection.css';

const FaqSection = () => {
  const [faq, setFaqs] = useState([null]);

  const toggleVisibility = 

  useEffect(() => {
    const fetchFaqs = async () => {
      try {
        const response = await axios.get('/api/faq');
        setFaqs(response.data);
      } catch (error) {
        console.error('Erro ao carregar FAQs:', error);
      }
    };

    fetchFaqs();
  }, []);

  const handleUpdate = async (id, updatedFaq) => {
    try {
      await axios.put(`/api/faq/${id}`, updatedFaq);
      setFaqs(faq.map(faq => (faq.id === id ? { ...faq, ...updatedFaq } : faq)));
    } catch (error) {
      console.error('Erro ao atualizar FAQ:', error);
    }
  };

  return (
    <div className="faq-section">
      <h2 className="section-title">Editar FAQs</h2>
      {faq.map(faq => (
        <div key={faq.id} className="faq-item">
          <input
            type="text"
            value={faq.question}
            onChange={(e) => handleUpdate(faq.id, { question: e.target.value })}
            className="faq-input"
            placeholder="Pergunta"
          />
          <textarea
            value={faq.answer}
            onChange={(e) => handleUpdate(faq.id, { answer: e.target.value })}
            className="faq-textarea"
            placeholder="Resposta"
          />
        </div>
      ))}
    </div>
  );
};

export default FaqSection;
