import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Materia.css';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';

const Materia = () => {
  const { id } = useParams(); // Pega o ID da URL
  const [materia, setMateria] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Faz a requisição para buscar a matéria pelo ID
    const fetchMateria = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/blogs/${id}`);
        setMateria(response.data);
      } catch (err) {
        setError('Erro ao carregar a matéria: ' + err.message);
      }
    };
    
    fetchMateria();
  }, [id]);

  if (error) {
    return <p>{error}</p>;
  }

  if (!materia) {
    return <p>Carregando...</p>;
  }

  return (
    <>
      <Header />
      <div className="blog-post">
        <h1 className="title">{materia.title}</h1>
        <div className="image-placeholder" style={{ backgroundImage: `url(${materia.image})` }}></div>
        <h1 className="titulo-materia">{materia.title}</h1>
        <hr />
        <div className="content">
          <p>{materia.description}</p>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Materia;
