import React, { useState, useEffect } from 'react';
import './faqSection.css';

const FaqSection = ({ faq, setFaqs}) => {
  const [activeIndex, setActiveIndex] = useState([null]);

  const toggleVisibility = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const handleInputChange = (index, field, value) => {
    const updateFaq = faq.map((faq, i) => 
      i === index ? { ...faq, [field]: value}: faq
    );
    setFaqs(updateFaq);
  }

  return (
    <div className="faq-section">
      <h2 className="section-title">Editar FAQs</h2>
      {faq.map((faq, index) => (
        <div key={index} className="faq-item">                       
                              
            <div
               className={`faq-item ${activeIndex === index ? 'active' : ''}`}
               onClick={() => toggleVisibility(index)}
            >
               {`Pergunta do Faq ${index + 1}: ${faq.pergunta}`}
               {`Resposta do Faq ${index + 1}: ${faq.resposta}`}
             </div>

          <input
            type="text"
            value={faq.pergunta}
            onChange={(e) => handleInputChange(faq.id, { pergunta: e.target.value })}
            className="faq-input"
            placeholder="Pergunta"
          />
          <textarea
            value={faq.resposta}
            onChange={(e) => handleInputChange(faq.id, { resposta: e.target.value })}
            className="faq-textarea"
            placeholder="Resposta"
          />
        </div>
      ))}
    </div>
  );
};

export default FaqSection;
