import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './CreateBlogSection.css';

const CreateBlogSection = ({ onCreate }) => {
    const [newBlog, setNewBlog] = useState({
        slug: '',
        title: '',
        description: '',
        content: '',
        image: '',
        imageBanner: '',
        image2: '',
        link: ''
    });

    const handleSlugChange = (e) => {
        const slug = e.target.value;
        const id = new Date().getTime(); // Usando timestamp como exemplo para gerar ID
        setNewBlog({
            ...newBlog,
            slug: slug,
            image: `https://bfbaby.com.br/up/blog-${id}.png`,
            imageBanner: `https://bfbaby.com.br/up/materia-${id}.png`,
            image2: `https://bfbaby.com.br/up/pageblog-${id}.png`,
            link: `/materia/${slug}`
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewBlog({ ...newBlog, [name]: value });
    };

    const handleContentChange = (content) => {
        setNewBlog({ ...newBlog, content });
    };

    const handleCreateBlog = () => {
        if (onCreate) {
            onCreate(newBlog);
        }
        // Limpar o formulário após criar o blog
        setNewBlog({
            slug: '',
            title: '',
            description: '',
            content: '',
            image: '',
            imageBanner: '',
            image2: '',
            link: ''
        });
    };

    return (
        <div className="create-blog-section">
            <h2 className="create-blog-title">Criar Novo Blog</h2>
            <div className="form-group">
                <input
                    type="text"
                    name="slug"
                    value={newBlog.slug}
                    onChange={handleSlugChange}
                    className="form-control"
                    placeholder="Slug"
                />
                <input
                    type="text"
                    name="title"
                    value={newBlog.title}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Título"
                />
                <textarea
                    name="description"
                    value={newBlog.description}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Descrição"
                />
                <ReactQuill
                    value={newBlog.content}
                    onChange={handleContentChange}
                    placeholder="Escreva a matéria do blog aqui..."
                    className="blog-quill-editor"
                />
                <input
                    type="text"
                    name="image"
                    value={newBlog.image}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="URL da Imagem"
                    readOnly
                />
                <input
                    type="text"
                    name="imageBanner"
                    value={newBlog.imageBanner}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="URL do Banner"
                    readOnly
                />
                <input
                    type="text"
                    name="image2"
                    value={newBlog.image2}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="URL da Segunda Imagem"
                    readOnly
                />
                <input
                    type="text"
                    name="link"
                    value={newBlog.link}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Link do Blog"
                    readOnly
                />
                <div className="button-group">
                    <button onClick={handleCreateBlog} className="create-button">Criar Blog</button>
                </div>
            </div>
        </div>
    );
};

export default CreateBlogSection;
