import './Desconto.css';

function Desconto(){
  const [descontoItems, setDescontoItems] = useState([]);

  useEffect(() => {
      fetchDescontoItems();
  }, []);

  const fetchDescontoItems = async () => {
      try {
          const response = await axios.get('http://localhost:3001/api/compare');
          setDescontoItems(response.data);
      } catch (error) {
          console.error("Erro ao buscar itens do Compare:", error);
      }
  };

    return(
        <div className="simple-section">
        <div className="content">
          <p className="text">
          Ganhe 10% de desconto para adquirir o primeiro colocado em nosso ranking dos melhores colchões.
          </p>
          <button className="button">Quero dormir melhor!</button>
        </div>
      </div>
    );
}

