import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Compare.css';

function Compare() {
    const [compareItems, setCompareItems] = useState([]);
    const [editedItem, setEditedItem] = useState(null);

    useEffect(() => {
        fetchCompareItems();
    }, []);

    const fetchCompareItems = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/compare');
            setCompareItems(response.data);
        } catch (error) {
            console.error("Erro ao buscar itens do Compare:", error);
        }
    };

    const handleEdit = (item) => {
        setEditedItem({ ...item });
    };

    const handleSave = async () => {
        if (editedItem) {
            try {
                await axios.put(`http://localhost:3001/api/compare/${editedItem.id}`, editedItem);
                alert("Item atualizado com sucesso!");
                fetchCompareItems(); // Atualiza a lista de itens após a edição
                setEditedItem(null); // Reseta o item editado
            } catch (error) {
                console.error("Erro ao atualizar item:", error);
                alert("Ocorreu um erro ao tentar atualizar o item.");
            }
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedItem({ ...editedItem, [name]: value });
    };

    return (
        <div>
            <h1 className='title'>Compare O Melhor Colchão</h1>

            {compareItems.map(item => (
                <div className='card' key={item.id}>
                    <img 
                        src={item.image} 
                        alt={item.product} 
                        className='image' 
                    />
                    <div className='card-content'>
                        <h2>{item.brand}</h2>
                        <h3>{item.product}</h3>

                        <ul className='features'>
                            {item.features.map((feature, index) => (
                                <li key={index}>
                                    <img src="https://bfbaby.com.br/up/check.png" alt='Check'/>
                                    {`${feature.feature} ${feature.rating}`}
                                </li>
                            ))}
                        </ul>
                    </div>
                    <div className='card-buttons'>
                        <div className='stars'>
                            <span>{'⭐'.repeat(item.rating)}</span>
                            <span className='review-number'>{item.reviewCount}</span>
                        </div>
                        <button className='primary-button' onClick={() => handleEdit(item)}>Editar</button>
                    </div>
                </div>
            ))}

            {editedItem && (
                <div className="edit-section">
                    <h2>Editando {editedItem.product}</h2>
                    <input 
                        type="text" 
                        name="brand" 
                        value={editedItem.brand} 
                        onChange={handleChange}
                        placeholder="Nome da marca"
                    />
                    <input 
                        type="text" 
                        name="product" 
                        value={editedItem.product} 
                        onChange={handleChange}
                        placeholder="Nome do produto"
                    />
                    <input 
                        type="text" 
                        name="image" 
                        value={editedItem.image} 
                        onChange={handleChange}
                        placeholder="URL da imagem"
                    />
                    {/* Você pode adicionar campos para editar as features e outros detalhes */}
                    <button className='save-button' onClick={handleSave}>Salvar</button>
                </div>
            )}
        </div>
    );
}

export default Compare;
