import React, { useState, useEffect } from "react";
import './BlogCarrossel.css';
import Slider from "react-slick";
import { Link } from 'react-router-dom';
import axios from 'axios';

function BlogCarrossel() {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/blogs');
                setBlogs(response.data);
            } catch (error) {
                console.error('Erro ao carregar os blogs:', error);
            }
        };

        fetchBlogs();
    }, []);

    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3.65,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    if (blogs.length === 0) {
        return <p>Carregando blogs...</p>;  // Exibe uma mensagem enquanto os blogs estão sendo carregados
    }

    return (
        <>
            <h1 className="BlogTitle">Blog</h1>

            <div className="slider-blog">
                <Slider {...settings} className="custom-slider-blog">
                    {blogs.map(blog => (
                        <div key={blog.slug} className="custom-card-blog">
                            <div className="card-image-blog" style={{ backgroundImage: `url(${blog.image})` }}></div>
                            <div className="card-content-blog">
                                <h2>{blog.title}</h2>
                                <p>{blog.description.substring(0, 100)}...</p>
                                <Link to={`/materia/${blog.slug}`}>
                                <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="red-more" />
                            </Link>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </>
    );
};

export default BlogCarrossel;
