import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './CreateBlogSection.css';

const CreateBlogSection = ({ onCreate }) => {
    const [newBlog, setNewBlog] = useState({
        slug: '',
        title: '',
        description: '',
        content: '',
    });

    const handleSlugChange = (e) => {
        const slug = e.target.value;
        setNewBlog({ ...newBlog, slug });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewBlog({ ...newBlog, [name]: value });
    };

    const handleContentChange = (content) => {
        setNewBlog({ ...newBlog, content });
    };

    const handleCreateBlog = () => {
        const id = new Date().getTime(); // Usando timestamp como exemplo para gerar ID
        const blogToCreate = {
            ...newBlog,
            image: `https://bfbaby.com.br/up/blog-${id}.png`,
            imageBanner: `https://bfbaby.com.br/up/materia-${id}.png`,
            image2: `https://bfbaby.com.br/up/pageblog-${id}.png`,
            link: `/materia/${newBlog.slug}`
        };

        console.log("Criando blog:", blogToCreate); // Verificar o conteúdo que está sendo enviado

        if (onCreate) {
            onCreate(blogToCreate);
        }

        setNewBlog({
            slug: '',
            title: '',
            description: '',
            content: '',
        });
    };

    return (
        <div className="create-blog-section">
            <h2 className="create-blog-title">Criar Novo Blog</h2>
            <div className="form-group">
                <input
                    type="text"
                    name="slug"
                    value={newBlog.slug}
                    onChange={handleSlugChange}
                    className="form-control"
                    placeholder="Slug"
                />
                <input
                    type="text"
                    name="title"
                    value={newBlog.title}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Título"
                />
                <textarea
                    name="description"
                    value={newBlog.description}
                    onChange={handleInputChange}
                    className="form-control"
                    placeholder="Descrição"
                />
                <ReactQuill
                    value={newBlog.content}
                    onChange={handleContentChange}
                    placeholder="Escreva a matéria do blog aqui..."
                    className="blog-quill-editor"
                />
                <div className="button-group">
                    <button onClick={handleCreateBlog} className="create-button">Criar Blog</button>
                </div>
            </div>
        </div>
    );
};

export default CreateBlogSection;
