import React from "react";
import { useParams } from "react-router-dom";
import './Materia.css';
import Header from "../../components/Header/Header";
import BlogCarrossel from "../../components/BlogCarrossel/BlogCarrossel";
import Footer from "../../components/Footer/Footer";

const BlogPost = () => {
    const { id } = useParams();  // Pega o ID da URL

    // Simulação de dados de matérias
    const materias = {
        1: {
            title: "O que é um colchão certificado e por que é importante pensar nisso na hora de dormir?",
            content: "Aqui está o conteúdo completo da matéria 1..."
        },
        // Adicione mais matérias conforme necessário
    };

    const materia = materias[id];

    return (
        <div className="blog-post">
            <Header />
            <div className="blog-header">
                <span className="author">Writer</span>
                <h1 className="title">{materia?.title || 'Matéria não encontrada'}</h1>
            </div>
            <div className="image-placeholder"></div>
            <div className="meta">
                <span className="time">5 min</span>
            </div>
            <hr />
            <div className="content">
                <p>{materia?.content || 'O conteúdo desta matéria não está disponível.'}</p>
            </div>
            <Footer />
        </div>
    );
}

export default BlogPost;
