import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './AdminPage.css';

const AdminPage = () => {
    const navigate = useNavigate();
    const [criterios, setCriterios] = useState([]);
    const [sliderData, setSliderData] = useState([]);
    const [selectedCriterio, setSelectedCriterio] = useState(null);
    const [selectedSlider, setSelectedSlider] = useState(null);
    const [originalCriterio, setOriginalCriterio] = useState(null);
    const [originalSlider, setOriginalSlider] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("authToken");
        if (!token) {
            navigate("/login");
        } else {
            fetchCriterios();
            fetchSliderData();
        }
    }, [navigate]);

    const fetchCriterios = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/criterios');
            setCriterios(response.data);
        } catch (error) {
            console.error("Erro ao buscar critérios:", error);
        }
    };

    const fetchSliderData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/slider');
            setSliderData(response.data);
        } catch (error) {
            console.error("Erro ao buscar dados do slider:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("authToken");
        navigate("/login");
    };

    const handleCriterioChange = (e) => {
        const criterioId = parseInt(e.target.value);
        const criterio = criterios.find(c => c.id === criterioId);
        setSelectedCriterio({ ...criterio });
        setOriginalCriterio({ ...criterio });  // Salva o estado original
    };

    const handleSliderChange = (e) => {
        const sliderId = parseInt(e.target.value);
        const slider = sliderData.find(s => s.id === sliderId);
        setSelectedSlider({ ...slider });
        setOriginalSlider({ ...slider });  // Salva o estado original
    };

    const handleCancel = (type) => {
        if (type === 'criterio') {
            setSelectedCriterio({ ...originalCriterio });
        } else if (type === 'slider') {
            setSelectedSlider({ ...originalSlider });
        }
    };

    const handleSave = async (type) => {
        const confirmSave = window.confirm("Você deseja confirmar a edição?");
    
        if (confirmSave) {
            try {
                if (type === 'criterio' && selectedCriterio) {
                    await axios.put(`http://localhost:3001/api/criterios/${selectedCriterio.id}`, {
                        title: selectedCriterio.title,
                        description: selectedCriterio.description
                    });
                } else if (type === 'slider' && selectedSlider) {
                    await axios.put(`http://localhost:3001/api/slider/${selectedSlider.id}`, {
                        title: selectedSlider.title,
                        description: selectedSlider.description
                    });
                }

                alert("Atualização realizada com sucesso!");
                fetchCriterios();
                fetchSliderData();
            } catch (error) {
                console.error("Erro ao salvar:", error);
                alert("Ocorreu um erro ao tentar atualizar.");
            }
        } else {
            handleCancel(type);
        }
    };

    return (
        <div className="admin-container">
            <h1 className="admin-title">Admin Dashboard</h1>
            <p className="admin-text">Selecione um critério ou item do slider para editar.</p>
            <button onClick={handleLogout} className="logout-button">Logout</button>

            <div className="criteria-section">
                <h2>Editar Critérios</h2>
                <select onChange={handleCriterioChange}>
                    <option value="">Selecione um critério</option>
                    {criterios.map(criterio => (
                        <option key={criterio.id} value={criterio.id}>{criterio.title}</option>
                    ))}
                </select>

                {selectedCriterio && (
                    <div className="edit-section">
                        <input
                            type="text"
                            name="title"
                            value={selectedCriterio.title}
                            onChange={(e) => setSelectedCriterio({ ...selectedCriterio, title: e.target.value })}
                            className="edit-input"
                        />
                        <textarea
                            name="description"
                            value={selectedCriterio.description}
                            onChange={(e) => setSelectedCriterio({ ...selectedCriterio, description: e.target.value })}
                            className="edit-textarea"
                        />
                        <div className="button-group">
                            <button onClick={() => handleSave('criterio')} className="save-button">Salvar</button>
                            <button onClick={() => handleCancel('criterio')} className="cancel-button">Cancelar</button>
                        </div>
                    </div>
                )}
            </div>

            <div className="slider-section">
                <h2>Editar Slider</h2>
                <select onChange={handleSliderChange}>
                    <option value="">Selecione um item do slider</option>
                    {sliderData.map(slider => (
                        <option key={slider.id} value={slider.id}>{slider.title}</option>
                    ))}
                </select>

                {selectedSlider && (
                    <div className="edit-section">
                        <input
                            type="text"
                            name="title"
                            value={selectedSlider.title}
                            onChange={(e) => setSelectedSlider({ ...selectedSlider, title: e.target.value })}
                            className="edit-input"
                        />
                        <textarea
                            name="description"
                            value={selectedSlider.description}
                            onChange={(e) => setSelectedSlider({ ...selectedSlider, description: e.target.value })}
                            className="edit-textarea"
                        />
                        <div className="button-group">
                            <button onClick={() => handleSave('slider')} className="save-button">Salvar</button>
                            <button onClick={() => handleCancel('slider')} className="cancel-button">Cancelar</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default AdminPage;
