import React, { useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './AdminPage.css';

const AdminPage = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("authToken");
        if (!token) {
            navigate("/login");
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem("authToken");
        navigate("/login");
    };

    const [criterios, setCriterios] = useState([]);
    const [editedCriterio, setEditedCriterio] = useState(null);

    useEffect(() => {
        fetchCriterios();
    }, []);

    const fetchCriterios = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/criterios');
            setCriterios(response.data);
        } catch (error) {
            console.error("Erro ao buscar critérios:", error);
        }
    };

    const handleEdit = (criterio) => {
        setEditedCriterio({ ...criterio }); // Cria uma cópia do critério para edição
    };

    const handleSave = async () => {
        try {
            await axios.put(`http://localhost:3001/api/criterios/${editedCriterio.id}`, editedCriterio);
            setEditedCriterio(null);
            fetchCriterios(); // Atualizar a lista após a edição
        } catch (error) {
            console.error("Erro ao salvar critério:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedCriterio({ ...editedCriterio, [name]: value });
    };

    return (
        <div className="admin-container">
            <h1 className="admin-title">Admin Dashboard</h1>
            <p className="admin-text">Gerencie os critérios de seleção.</p>
            <button onClick={handleLogout} className="logout-button">Logout</button> {/* Botão de Logout */}
            
            <div className="criteria-list">
                {criterios.map(criterio => (
                    <div key={criterio.id} className="criteria-item">
                        {editedCriterio && editedCriterio.id === criterio.id ? (
                            <div>
                                <input
                                    type="text"
                                    name="title"
                                    value={editedCriterio.title}
                                    onChange={handleChange}
                                    className="edit-input"
                                />
                                <textarea
                                    name="description"
                                    value={editedCriterio.description}
                                    onChange={handleChange}
                                    className="edit-textarea"
                                />
                                <button onClick={handleSave} className="save-button">Salvar</button>
                            </div>
                        ) : (
                            <div>
                                <h3>{criterio.title}</h3>
                                <p>{criterio.description}</p>
                                <button onClick={() => handleEdit(criterio)} className="edit-button">Editar</button>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AdminPage;
