import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './BlogsSection.css';

const BlogAccordionSection = ({ blogs, updateBlog, handleSave }) => {
    const [activeIndex, setActiveIndex] = useState(null);

    // Alterna a visibilidade do conteúdo da seção ativa
    const toggleAccordion = (index) => {
        setActiveIndex(activeIndex === index ? null : index);
    };

    const handleContentChange = (id, content) => {
        updateBlog(id, 'content', content);
    };

    return (
        <div className="blog-accordion-section">
            <h2 className="blog-accordion-title">Editar Blogs</h2>
            {blogs.map((blog, index) => (
                <div key={blog.id} className="blog-accordion-item">
                    <div 
                        className={`blog-accordion-header ${activeIndex === index ? 'active' : ''}`} 
                        onClick={() => toggleAccordion(index)}
                    >
                        Blog {index + 1}
                    </div>
                    {activeIndex === index && (
                        <div className="blog-accordion-content">
                            <input
                                type="text"
                                value={blog.slug}
                                onChange={(e) => updateBlog(blog.id, 'slug', e.target.value)}
                                className="blog-accordion-input"
                                placeholder="Slug"
                            />
                            <input
                                type="text"
                                value={blog.title}
                                onChange={(e) => updateBlog(blog.id, 'title', e.target.value)}
                                className="blog-accordion-input"
                                placeholder="Título"
                            />
                            <textarea
                                value={blog.description}
                                onChange={(e) => updateBlog(blog.id, 'description', e.target.value)}
                                className="blog-accordion-textarea"
                                placeholder="Descrição"
                            />
                            <ReactQuill 
                                value={blog.content}
                                onChange={(content) => handleContentChange(blog.id, content)}
                                placeholder="Escreva a matéria do blog aqui..."
                                className="blog-accordion-quill"
                            />
                            <input
                                type="text"
                                value={blog.image}
                                onChange={(e) => updateBlog(blog.id, 'image', e.target.value)}
                                className="blog-accordion-input"
                                placeholder="URL da Imagem"
                            />
                            <input
                                type="text"
                                value={blog.link}
                                onChange={(e) => updateBlog(blog.id, 'link', e.target.value)}
                                className="blog-accordion-input"
                                placeholder="Link do Blog"
                            />
                        </div>
                    )}
                </div>
            ))}
            <div className="blog-accordion-button-group">
                <button onClick={() => handleSave('blogs')} className="blog-accordion-save-button">Salvar</button>
            </div>
        </div>
    );
};

export default BlogAccordionSection;
