import React, { useEffect, useState } from "react";
import './Review.css';
import Header from "../../components/Header/Header";
import BlogCarrossel from "../../components/BlogCarrossel/BlogCarrossel";
import Footer from "../../components/Footer/Footer";
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Review = () => {
    const { id } = useParams();
    const [review, setReview] = useState(null);

    useEffect(() => {
        const fetchReview = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/api/reviews/${id}`);
                setReview(response.data);
            } catch (error) {
                console.error("Erro ao buscar review", error);
            }
        };

        fetchReview();
    }, [id]);

    if (!review) {
        return <p>Carregando...</p>;
    }

    return (
        <>
            <Header />
            <div className="Review-colchao">
                <a href="/">
                    <img src="https://bfbaby.com.br/up/voltar.png" alt="voltar" className="back" />
                </a>
                <h1 className="title-review">{review.titulo}</h1>
                
                <div className="conclusao">
                    <h1>Conclusão</h1>
                    <p>{review.conclusao}</p>
                </div>

                <div className="sobre-a-marca">
                    <h1>Sobre a marca</h1>
                    <p>{review.sobre_marca}</p>
                </div>

                <div className="vantagens-desvantagens">
                    <div className="vantagens">
                        <h1>Vantagens</h1>
                        <ul>
                            {review.vantagens.map((vantagem, index) => (
                                <li key={index}><span className="material-symbols-outlined check">check_circle</span> {vantagem}</li>
                            ))}
                        </ul>
                    </div>
                    <div className="desvantagens">
                        <h1>Desvantagens</h1>
                        <ul>
                            {review.desvantagens.map((desvantagem, index) => (
                                <li key={index}><span className="material-symbols-outlined cancel">cancel</span> {desvantagem}</li>
                            ))}
                        </ul>
                    </div>
                </div>

                <div className="sobre-o-colchao">
                    <h1>Sobre o colchão</h1>
                    <p>{review.sobre_colchao}</p>
                </div>
            </div>
            <BlogCarrossel />
            <Footer />
        </>
    );
}

export default Review;
