import React, { useState, useEffect } from 'react';
import './faqSection.css';

const FaqSection = () => {
  const [ac, setFaqs] = useState([null]);

  const toggleVisibility = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const handleInputChange = (index, field, value) => {
    const updateFaq = faq.map((map, i) => 
      i === index ? { ...faq, [field]: value}: faq
    );
    setFaqs(updateFaq);
  }

  return (
    <div className="faq-section">
      <h2 className="section-title">Editar FAQs</h2>
      {faq.map(faq => (
        <div key={faq.id} className="faq-item">
          <input
            type="text"
            value={faq.question}
            onChange={(e) => handleUpdate(faq.id, { question: e.target.value })}
            className="faq-input"
            placeholder="Pergunta"
          />
          <textarea
            value={faq.answer}
            onChange={(e) => handleUpdate(faq.id, { answer: e.target.value })}
            className="faq-textarea"
            placeholder="Resposta"
          />
        </div>
      ))}
    </div>
  );
};

export default FaqSection;
