import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './AdminPage.css';

const AdminPage = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("authToken");
        if (!token) {
            navigate("/login");
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem("authToken");
        navigate("/login");
    };

    const [criterios, setCriterios] = useState([]);
    const [sliderData, setSliderData] = useState([]);
    const [editedItem, setEditedItem] = useState(null);

    useEffect(() => {
        fetchCriterios();
        fetchSliderData();
    }, []);

    const fetchCriterios = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/criterios');
            setCriterios(response.data);
        } catch (error) {
            console.error("Erro ao buscar critérios:", error);
        }
    };

    const fetchSliderData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/slider');
            setSliderData(response.data);
        } catch (error) {
            console.error("Erro ao buscar dados do slider:", error);
        }
    };

    const handleEdit = (item, type) => {
        setEditedItem({ ...item, type }); // Adiciona o tipo para saber se é critério ou slider
    };

    const handleSave = async () => {
        const confirmSave = window.confirm("Você deseja confirmar a edição?");
    
        if (confirmSave) {
            try {
                if (editedItem.type === 'criterio') {
                    await axios.put(`http://localhost:3001/api/criterios/${editedItem.id}`, {
                        title: editedItem.title,
                        description: editedItem.description
                    });
                } else if (editedItem.type === 'slider') {
                    await axios.put(`http://localhost:3001/api/slider/${editedItem.id}`, {
                        title: editedItem.title,
                        description: editedItem.description
                    });
                }
                
                setEditedItem(null);
                fetchCriterios();
                fetchSliderData();
                alert("Atualização realizada com sucesso!");
            } catch (error) {
                console.error("Erro ao salvar:", error);
                alert("Ocorreu um erro ao tentar atualizar.");
            }
        } else {
            alert("Edição cancelada.");
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedItem({ ...editedItem, [name]: value });
    };

    return (
        <div className="admin-container">
            <h1 className="admin-title">Admin Dashboard</h1>
            <p className="admin-text">Gerencie os critérios de seleção e o slider.</p>
            <button onClick={handleLogout} className="logout-button">Logout</button>
            
            <div className="criteria-list">
                <h2>Critérios</h2>
                {criterios.map(criterio => (
                    <div key={criterio.id} className="criteria-item">
                        {editedItem && editedItem.id === criterio.id && editedItem.type === 'criterio' ? (
                            <div>
                                <input
                                    type="text"
                                    name="title"
                                    value={editedItem.title}
                                    onChange={handleChange}
                                    className="edit-input"
                                />
                                <textarea
                                    name="description"
                                    value={editedItem.description}
                                    onChange={handleChange}
                                    className="edit-textarea"
                                />
                                <button onClick={handleSave} className="save-button">Salvar</button>
                            </div>
                        ) : (
                            <div>
                                <h3>{criterio.title}</h3>
                                <p>{criterio.description}</p>
                                <button onClick={() => handleEdit(criterio, 'criterio')} className="edit-button">Editar</button>
                            </div>
                        )}
                    </div>
                ))}
            </div>

            <div className="slider-list">
                <h2>Slider</h2>
                {sliderData.map(slider => (
                    <div key={slider.id} className="criteria-item">
                        {editedItem && editedItem.id === slider.id && editedItem.type === 'slider' ? (
                            <div>
                                <input
                                    type="text"
                                    name="title"
                                    value={editedItem.title}
                                    onChange={handleChange}
                                    className="edit-input"
                                />
                                <textarea
                                    name="description"
                                    value={editedItem.description}
                                    onChange={handleChange}
                                    className="edit-textarea"
                                />
                                <button onClick={handleSave} className="save-button">Salvar</button>
                            </div>
                        ) : (
                            <div>
                                <h3>{slider.title}</h3>
                                <p>{slider.description}</p>
                                <button onClick={() => handleEdit(slider, 'slider')} className="edit-button">Editar</button>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AdminPage;
