import React, { useState, useEffect } from "react";
import './Blog.css';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import axios from 'axios';

function Blog() {
    const [latestBlog, setLatestBlog] = useState(null);

    useEffect(() => {
        const fetchLatestBlog = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/blogs');
                const blogs = response.data;

                if (blogs.length > 0) {
                    const blogWithMaxId = blogs.reduce((max, blog) => blog.id > max.id ? blog : max, blogs[0]);
                    setLatestBlog(blogWithMaxId);
                }
            } catch (error) {
                console.error("Erro ao buscar os blogs:", error);
            }
        };

        fetchLatestBlog();
    }, []);

    if (!latestBlog) {
        return <p>Carregando...</p>;
    }

    return (
        <>
            <Header />
            <div className="blog-container">
                <a href="/">
                    <img src="https://bfbaby.com.br/up/voltar.png" alt="voltar" className="back" />
                </a>
                <h1 className="title">Blog</h1>
                <div className="images-placeholder"></div>
                <div className="blog-content">
                    <h1>{latestBlog.title}</h1>
                    <p>{latestBlog.description}</p>
                    <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="leiMais" id="faq" />
                </div>
            </div>
            <Footer />
        </>
    );
}

export default Blog;
