import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Orgaos.css';

function Orgaos() {
    const [orgaos, setOrgaos] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3001/api/orgaos')
            .then(response => {
                setOrgaos(response.data);
            })
            .catch(error => {
                console.error("Erro ao buscar órgãos reguladores:", error);
            });
    }, []);

    return (
        <div className='orgaos-section'>
            <h2 className="info-text">Órgãos reguladores</h2>
            <p className='subinfo'>Conheça os órgãos que certificam o mercado de colchões</p>
            <div className='orgaos-list'>
                {orgaos.map(orgao => (
                    <div className='orgaos-item' key={orgao.id}>
                        <h3 className='orgaos-tittle'>
                            <img src={`https://bfbaby.com.br/up/orgao-${orgao.id}.png`} alt={`Ícone ${orgao.title}`} className="orgao-icon" />
                            {orgao.title}
                        </h3>
                        <p className='orgaos-description' id='Compare'>
                            {orgao.description}
                        </p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Orgaos;
