import React, { useState } from 'react';
import './OrgaosSection.css';

const OrgaosSection = ({ orgaos, setOrgaos }) => {
    const [activeIndex, setActiveIndex] = useState(null);

    const toggleVisibility = (index) => {
        setActiveIndex(activeIndex === index ? null : index);
    };

    const handleInputChange = (index, field, value) => {
        const updatedOrgaos = orgaos.map((orgao, i) =>
            i === index ? { ...orgao, [field]: value } : orgao
        );
        setOrgaos(updatedOrgaos);
    };

    return (
        <div className="orgaos-section-admin">
            <h2 className="section-title-admin">Editar Órgãos Regulamentadores</h2>
            {orgaos.map((orgao, index) => (
                <div key={index}>
                    <div
                        className={`orgaos-item ${activeIndex === index ? 'active' : ''}`}
                        onClick={() => toggleVisibility(index)}
                    >
                        {`Órgão Regulamentador ${index + 1}: ${orgao.title}`}
                    </div>
                    {activeIndex === index && (
                        <div className="orgaos-edit-section">
                            <input
                                type="text"
                                value={orgao.title}
                                onChange={(e) => handleInputChange(index, 'title', e.target.value)}
                                className="orgaos-edit-input"
                                placeholder="Título"
                            />
                            <textarea
                                value={orgao.description}
                                onChange={(e) => handleInputChange(index, 'description', e.target.value)}
                                className="orgaos-edit-textarea"
                                placeholder="Descrição"
                            />
                        </div>
                    )}
                </div>
            ))}
            <div className="button-group">
                <button className="save-button">Salvar</button>
            </div>
        </div>
    );
};

export default OrgaosSection;
