import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Review.css';
import Header from "../../components/Header/Header";
import BlogCarrossel from "../../components/BlogCarrossel/BlogCarrossel";
import Footer from "../../components/Footer/Footer";

const Review = () => {
    const { url } = useParams();
    const [review, setReview] = useState(null);

    useEffect(() => {
        const fetchReview = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/api/review/${url}`);
                setReview(response.data);
            } catch (error) {
                console.error("Erro ao buscar review:", error);
            }
        };

        fetchReview();
    }, [url]);

    if (!review) return <p>Carregando...</p>;

    return (
        <>
            <Header />
            <div className="Review-colchao">
                <a href="/">
                    <img src="https://bfbaby.com.br/up/voltar.png" alt="voltar" className="back" />
                </a>
                <h1 className="title-review">{review.titulo}</h1>
                <div className="review-content">
                    <div>
                        <img className="review-placeholder" />
                    </div>
                    <div className="top1">
                        <div className="top1-image">
                            <img 
                                src="https://bfbaby.com.br/up/tag.png"
                                alt="tag"
                                className="tag-review"
                            />
                        </div>
                        <div className="top1-content">
                            <h2>BF Colchões</h2>
                            <p>{review.conclusao}</p>
                        </div>
                        <button className="ver-produto">Ver produto</button>
                    </div>
                </div>
                <hr />
                {/* Renderize outras partes do review aqui */}
            </div>
            <BlogCarrossel />
            <Footer />
        </>
    );
}

export default Review;
