import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Slider"; // Certifique-se de ter o arquivo CSS importado

function CustomSlider() {
    const settings = {
        dots: true,
        infinity: true,
        slidesToShow: 1,
        slidesToScroll: 1,
    };

    const data = [
        {
            tittle: `card 1`,
            image: `imagem do card 1`,
            description: `descrição do card 1`,
        },
        {
            tittle: `card 2`,
            image: `imagem do card 2`,
            description: `descrição do card 2`,
        },
        {
            tittle: `card 3`,
            image: `imagem do card 3`,
            description: `descrição do card 3`,
        },
        {
            tittle: `card 4`,
            image: `imagem do card 4`,
            description: `descrição do card 4`,
        },
        {
            tittle: `card 5`,
            image: `imagem do card 5`,
            description: `descrição do card 5`,
        },
        {
            tittle: `card 6`,
            image: `imagem do card 6`,
            description: `descrição do card 6`,
        },
        {
            tittle: `card 7`,
            image: `imagem do card 7`,
            description: `descrição do card 7`,
        },
    ]

    return (
        <div className="container">
            <div className="slider-container">
                <Slider {...settings}>
                    {data.map((d) => (
                        <div className="card" key={d.tittle}>
                            <div className="card-image-container">
                                <img src={d.image} alt="" className="card-image" />
                            </div>

                            <div className="card-content">
                                <p className="card-title">{d.tittle}</p>
                                <p>{d.description}</p>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
}

export default CustomSlider;
