import './CompareSection.css';import React, { useState } from 'react';

const CompareSection = ({ compareItems, setCompareItems, handleSave }) => {
  
  const handleInputChange = (id, field, value) => {
    const updatedCompareItems = compareItems.map(item =>
      item.id === id ? { ...item, [field]: value } : item
    );
    setCompareItems(updatedCompareItems);
  };

  const handleFeaturesChange = (id, newFeatures) => {
    const updatedCompareItems = compareItems.map(item => 
      item.id === id ? { ...item, features: newFeatures.split('\n').map(feature => feature.trim()) } : item
    );
    setCompareItems(updatedCompareItems);
  };

  const toggleVisibility = (id) => {
    setCompareItems(compareItems.map(item => 
      item.id === id ? { ...item, isVisible: !item.isVisible } : item
    ));
  };

  return (
    <div className="compare-section">
      <h2 className="section-title">Editar Itens do Compare</h2>
      {compareItems.map((item, index) => (
        <div key={item.id} className="compare-edit-section">
          <h3 className="compare-item-label" onClick={() => toggleVisibility(item.id)}>
            Item Compare {index + 1}: {item.product}
          </h3>
          {item.isVisible && (
            <>
              <input
                type="text"
                value={item.brand}
                onChange={(e) => handleInputChange(item.id, 'brand', e.target.value)}
                className="compare-edit-input"
                placeholder="Marca"
              />
              <input
                type="text"
                value={item.product}
                onChange={(e) => handleInputChange(item.id, 'product', e.target.value)}
                className="compare-edit-input"
                placeholder="Produto"
              />
              <input
                type="text"
                value={item.reviewCount}
                onChange={(e) => handleInputChange(item.id, 'reviewCount', e.target.value)}
                className="compare-edit-input"
                placeholder="Contagem de Avaliações"
              />
              <textarea
                value={item.features.join('\n')}
                onChange={(e) => handleFeaturesChange(item.id, e.target.value)}
                className="compare-edit-textarea"
                placeholder="Features (uma por linha)"
              />
            </>
          )}
        </div>
      ))}
      <div className="button-group">
        <button onClick={handleSave} className="save-button">Salvar</button>
      </div>
    </div>
  );
};

export default CompareSection;
