import React from 'react';
import './Header.css';
import { Link } from 'react-router-dom';

function Header() {
  // Função para rolar suavemente até a seção alvo
  const handleScroll = (event, targetId) => {
    event.preventDefault();
    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      targetElement.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <header className="header">
      <a href="/" ><img 
        src='https://bfbaby.com.br/up/logo.png'
        alt='Logo O Melhor Colchão'
        className="logo-text"
      /></a>
      <nav className="nav-links">
        <a href="#Compare" onClick={(e) => handleScroll(e, 'Compare')}>Ranking</a>
        <Link to='/blog'>Blog</Link>
        <a href="#faq" onClick={(e) => handleScroll(e, 'faq')}>Perguntas Frequentes</a>
      </nav>
    </header>
  );
}

export default Header;
