import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Compare.css';

function Compare() {
    const [compareItems, setCompareItems] = useState([]);

    useEffect(() => {
        fetchCompareItems();
    }, []);

    const fetchCompareItems = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/compare');
            setCompareItems(response.data);
        } catch (error) {
            console.error("Erro ao buscar itens do Compare:", error);
        }
    };

    return (
        <div>
            <h1 className='title' id='Compare'>Compare O Melhor Colchão</h1>

            {compareItems.map(item => (
                <div className='card' key={item.id}>
                    <img 
                        src={item.image} 
                        alt={item.product} 
                        className='image' 
                    />
                    <div className='card-content'>
                        <h2>{item.brand}</h2>
                        <h3>{item.product}</h3>

                        <ul className='features'>
                            {item.features.map((feature, index) => (
                                <li key={index}>
                                    <img src="https://bfbaby.com.br/up/check.png" alt='Check'/>
                                    {`${feature.feature} ${feature.rating}`}
                                </li>
                            ))}
                        </ul>
                    </div>
                    <div className='card-buttons'>
                        <div className='stars'>
                            <img src={item.rating} alt="Estrelas" className='rating-image' />
                            <span className='review-number'>{item.reviewCount}</span>
                        </div>
                        <button className='primary-button'>Botão</button>
                        <button className='secondary-button'>Botão</button>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default Compare;
