import React from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './CreateBlogSection.css';

const CreateBlogSection = ({ newBlog, setNewBlog, handleCreateBlog }) => {
    return (
        <div className="create-blog-section">
            <h2 className="section-title">Criar Novo Blog</h2>
            <input
                type="text"
                value={newBlog.slug}
                onChange={(e) => setNewBlog({ ...newBlog, slug: e.target.value })}
                className="create-blog-input"
                placeholder="Slug"
            />
            <input
                type="text"
                value={newBlog.title}
                onChange={(e) => setNewBlog({ ...newBlog, title: e.target.value })}
                className="create-blog-input"
                placeholder="Título"
            />
            <textarea
                value={newBlog.description}
                onChange={(e) => setNewBlog({ ...newBlog, description: e.target.value })}
                className="create-blog-textarea"
                placeholder="Descrição"
            />
            <ReactQuill 
                value={newBlog.content}
                onChange={(content) => setNewBlog({ ...newBlog, content })}
                placeholder="Escreva a matéria do blog aqui..."
                className="blog-edit-quill"
            />
            <input
                type="text"
                value={newBlog.image}
                onChange={(e) => setNewBlog({ ...newBlog, image: e.target.value })}
                className="create-blog-input"
                placeholder="URL da Imagem"
            />
            <input
                type="text"
                value={newBlog.imageBanner}
                onChange={(e) => setNewBlog({ ...newBlog, imageBanner: e.target.value })}
                className="create-blog-input"
                placeholder="URL do Banner"
            />
            <input
                type="text"
                value={newBlog.image2}
                onChange={(e) => setNewBlog({ ...newBlog, image2: e.target.value })}
                className="create-blog-input"
                placeholder="URL da Segunda Imagem"
            />
            <input
                type="text"
                value={newBlog.link}
                onChange={(e) => setNewBlog({ ...newBlog, link: e.target.value })}
                className="create-blog-input"
                placeholder="Link do Blog"
            />
            <div className="button-group">
                <button onClick={handleCreateBlog} className="save-button">Criar Blog</button>
            </div>
        </div>
    );
};

export default CreateBlogSection;
