import React from 'react';
import './Slider.css'

function Slider() {
    return(

        <div className="w-3/4 m-auto">
            <div className="mt-20">
                {data.map((d)=>(
                    <div className="bg-white h-[450px] text-black rounded-xl">
                        <div className="rounded-t-xl bg-indigo-500 flex justfy-center items-center">
                            <img src={d.image} alt="" className="h-44 w-44 rounded-full"/>
                        </div>   

                        <div className='flex flex-col '>
                            <p>{d.tittle}</p>
                            <p>{d.description}</p>
                        </div>  
                    </div>   
                ))}
            </div>
        </div>   

    );
}

const data = [
    {
        tittle: `card 1`,
        image: `imagem do card 1`,
        description: `descrição do card 1`,
    },
    {
        tittle: `card 2`,
        image: `imagem do card 2`,
        description: `descrição do card 2`,
    },
    {
        tittle: `card 3`,
        image: `imagem do card 3`,
        description: `descrição do card 3`,
    },
    {
        tittle: `card 4`,
        image: `imagem do card 4`,
        description: `descrição do card 4`,
    },
    {
        tittle: `card 5`,
        image: `imagem do card 5`,
        description: `descrição do card 5`,
    },
    {
        tittle: `card 6`,
        image: `imagem do card 6`,
        description: `descrição do card 6`,
    },
    {
        tittle: `card 7`,
        image: `imagem do card 7`,
        description: `descrição do card 7`,
    },
]

export default Slider;
