import React, { useEffect, useState } from "react";
import './BlogCarrossel.css';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Link } from 'react-router-dom';
import axios from 'axios';

function PrevArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-prev-arrow`}
            style={{ ...style, display: "block" }}
            onClick={onClick}
        />
    );
}

function NextArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-next-arrow`}
            style={{ ...style, display: "block" }}
            onClick={onClick}
        />
    );
}

function BlogCarrossel() {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        // Carregar dados da API
        axios.get('http://localhost:3001/api/blogs')
            .then(response => setBlogs(response.data))
            .catch(error => console.error('Erro ao carregar blogs:', error));
    }, []);

    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3.65,
        slidesToScroll: 1,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return (
        <>
            <h1 className="BlogTitle">Blog</h1>

            <div className="slider-blog">
                <Slider {...settings} className="custom-slider-blog">
                    {blogs.map(blog => (
                        <div className="custom-card-blog" key={blog.slug}>
                            <div
                                className="card-image-blog"
                                style={{ backgroundImage: `url(${blog.image})` }}
                            ></div>
                            <div className="card-content-blog">
                                <h2>{blog.title}</h2>
                                <p>{blog.description}</p>
                                <Link to={`/materia/${blog.slug}`}>
                                    <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="red-more" />
                                </Link>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </>
    );
}

export default BlogCarrossel;
