function BlogCarrossel(){
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3.65,
        slidesToScroll: 1,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    const slides = [
        {
            id: 1,
            image: "https://bfbaby.com.br/up/blog-1.jpg",
            title: "O que é um colchão certificado?",
            description: "Entenda a importância das certificações..."
        },
        // Adicione mais slides conforme necessário
    ];

    return(
    <>
        <h1 className="BlogTitle">Blog</h1>

        <div className="slider-blog">
            <Slider {...settings} className="custom-slider-blog">
                {slides.map(slide => (
                    <div key={slide.id} className="custom-card-blog">
                        <div className="card-image-blog" style={{ backgroundImage: `url(${slide.image})` }}></div>
                        <div className="card-content-blog">
                            <h2>{slide.title}</h2>
                            <p>{slide.description}</p>
                            <Link to="/Materia">
                                <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="red-more" />
                            </Link>
                        </div>
                    </div>
                ))}
            </Slider>
        </div>
    </>
    );
}
