import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './AdminPage.css';
import debounce from 'lodash/debounce';

const AdminPage = () => {
    const navigate = useNavigate();
    const [criterios, setCriterios] = useState([]);
    const [sliderData, setSliderData] = useState([]);
    const [compareItems, setCompareItems] = useState([]);
    const [orgaos, setOrgaos] = useState([]);
    const [descontoData, setDescontoData] = useState(null);
    const [blogs, setBlogs] = useState([]);
    const [selectedSection, setSelectedSection] = useState('home');

    useEffect(() => {
        const token = localStorage.getItem("authToken");
        if (!token) {
            navigate("/login");
        } else {
            fetchCriterios();
            fetchSliderData();
            fetchCompareItems();
            fetchOrgaos();
            fetchDescontoData();
            fetchBlogs();
        }
    }, [navigate]);

    const fetchCriterios = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/criterios');
            setCriterios(response.data);
        } catch (error) {
            console.error("Erro ao buscar critérios:", error);
        }
    };

    const fetchSliderData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/slider');
            setSliderData(response.data);
        } catch (error) {
            console.error("Erro ao buscar dados do slider:", error);
        }
    };

    const fetchCompareItems = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/compare');
            setCompareItems(response.data);
        } catch (error) {
            console.error("Erro ao buscar itens do Compare:", error);
        }
    };

    const fetchOrgaos = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/orgaos');
            setOrgaos(response.data);
        } catch (error) {
            console.error("Erro ao buscar órgãos:", error);
        }
    };

    const fetchDescontoData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/desconto');
            setDescontoData(response.data[0]);
        } catch (error) {
            console.error("Erro ao buscar desconto:", error);
        }
    };

    const fetchBlogs = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/blogs');
            setBlogs(response.data);
        } catch (error) {
            console.error("Erro ao buscar blogs:", error);
        }
    };

    const renderSection = () => {
        switch (selectedSection) {
            case 'home':
                return <p className="admin-home-text">Bem-vindo ao painel de administração! Selecione uma seção para editar.</p>;
            case 'criterios':
                return (
                    <div className="criterios-section">
                        <h2 className="section-title">Editar Critérios</h2>
                        {criterios.map((criterio, index) => (
                            <div key={criterio.id} className="criterios-edit-section">
                                <h3 className="criterios-item-label">Critério {index + 1}</h3>
                                <input
                                    type="text"
                                    value={criterio.title}
                                    onChange={(e) => updateCriterio(criterio.id, 'title', e.target.value)}
                                    className="criterios-edit-input"
                                    placeholder="Título"
                                />
                                <textarea
                                    value={criterio.description}
                                    onChange={(e) => updateCriterio(criterio.id, 'description', e.target.value)}
                                    className="criterios-edit-textarea"
                                    placeholder="Descrição"
                                />
                            </div>
                        ))}
                        <div className="button-group">
                            <button onClick={() => handleSave('criterios')} className="save-button">Salvar</button>
                        </div>
                    </div>
                );
            case 'slider':
                return (
                    <div className="slider-section">
                        <h2 className="section-title">Editar Slider</h2>
                        {sliderData.map((slider, index) => (
                            <div key={slider.id} className="slider-edit-section">
                                <h3 className="slider-item-label">Slider {index + 1}</h3>
                                <input
                                    type="text"
                                    value={slider.title}
                                    onChange={(e) => updateSlider(slider.id, 'title', e.target.value)}
                                    className="slider-edit-input"
                                    placeholder="Título"
                                />
                                <textarea
                                    value={slider.description}
                                    onChange={(e) => updateSlider(slider.id, 'description', e.target.value)}
                                    className="slider-edit-textarea"
                                    placeholder="Descrição"
                                />
                            </div>
                        ))}
                        <div className="button-group">
                            <button onClick={() => handleSave('slider')} className="save-button">Salvar</button>
                        </div>
                    </div>
                );
            case 'compare':
                return (
                    <div className="compare-section">
                        <h2 className="section-title">Editar Itens do Compare</h2>
                        {compareItems.map((compare, index) => (
                            <div key={compare.id} className="compare-edit-section">
                                <h3 className="compare-item-label">Item Compare {index + 1}</h3>
                                <input
                                    type="text"
                                    value={compare.brand}
                                    onChange={(e) => updateCompare(compare.id, 'brand', e.target.value)}
                                    className="compare-edit-input"
                                    placeholder="Marca"
                                />
                                <input
                                    type="text"
                                    value={compare.product}
                                    onChange={(e) => updateCompare(compare.id, 'product', e.target.value)}
                                    className="compare-edit-input"
                                    placeholder="Produto"
                                />
                                <input
                                    type="text"
                                    value={compare.reviewCount}
                                    onChange={(e) => updateCompare(compare.id, 'reviewCount', e.target.value)}
                                    className="compare-edit-input"
                                    placeholder="Contagem de Avaliações"
                                />
                            </div>
                        ))}
                        <div className="button-group">
                            <button onClick={() => handleSave('compare')} className="save-button">Salvar</button>
                        </div>
                    </div>
                );
            case 'orgaos':
                return (
                    <div className="orgaos-section">
                        <h2 className="section-title">Editar Órgãos Regulamentadores</h2>
                        {orgaos.map((orgao, index) => (
                            <div key={orgao.id} className="orgaos-edit-section">
                                <h3 className="orgaos-item-label">Órgão Regulamentador {index + 1}</h3>
                                <input
                                    type="text"
                                    value={orgao.title}
                                    onChange={(e) => updateOrgao(orgao.id, 'title', e.target.value)}
                                    className="orgaos-edit-input"
                                    placeholder="Título"
                                />
                                <textarea
                                    value={orgao.description}
                                    onChange={(e) => updateOrgao(orgao.id, 'description', e.target.value)}
                                    className="orgaos-edit-textarea"
                                    placeholder="Descrição"
                                />
                            </div>
                        ))}
                        <div className="button-group">
                            <button onClick={() => handleSave('orgaos')} className="save-button">Salvar</button>
                        </div>
                    </div>
                );
            case 'desconto':
                return (
                    <div className="desconto-section">
                        <h2 className="section-title">Editar Desconto</h2>
                        {descontoData && (
                            <div className="desconto-edit-section">
                                <h3 className="desconto-item-label">Desconto</h3>
                                <textarea
                                    value={descontoData.description}
                                    onChange={(e) => setDescontoData({ ...descontoData, description: e.target.value })}
                                    className="desconto-edit-textarea"
                                    placeholder="Descrição"
                                />
                                <input
                                    type="text"
                                    value={descontoData.botao}
                                    onChange={(e) => setDescontoData({ ...descontoData, botao: e.target.value })}
                                    className="desconto-edit-input"
                                    placeholder="Texto do Botão"
                                />
                            </div>
                        )}
                        <div className="button-group">
                            <button onClick={() => handleSave('desconto')} className="save-button">Salvar</button>
                        </div>
                    </div>
                );
            case 'blogs':
                return (
                    <div className="blog-section">
                        <h2 className="section-title">Editar Blogs</h2>
                        {blogs.map((blog, index) => (
                            <div key={blog.id} className="blog-edit-section">
                                <h3 className="blog-item-label">Blog {index + 1}</h3>
                                <input
                                    type="text"
                                    value={blog.slug}
                                    onChange={(e) => updateBlog(blog.id, 'slug', e.target.value)}
                                    className="blog-edit-input"
                                    placeholder="Slug"
                                />
                                <input
                                    type="text"
                                    value={blog.title}
                                    onChange={(e) => updateBlog(blog.id, 'title', e.target.value)}
                                    className="blog-edit-input"
                                    placeholder="Título"
                                />
                                <textarea
                                    value={blog.description}
                                    onChange={(e) => updateBlog(blog.id, 'description', e.target.value)}
                                    className="blog-edit-textarea"
                                    placeholder="Descrição"
                                />
                                <ReactQuill 
                                    value={blog.content}
                                    onChange={(content) => handleContentChange(blog.id, content)}
                                    placeholder="Escreva a matéria do blog aqui..."
                                    className="blog-edit-quill"
                                />
                            </div>
                        ))}
                        <div className="button-group">
                            <button onClick={() => handleSave('blogs')} className="save-button">Salvar</button>
                        </div>
                    </div>
                );
            default:
                return <p className="admin-home-text">Selecione uma seção no menu lateral.</p>;
        }
    };

    const updateCriterio = (id, field, value) => {
        const updatedCriterios = criterios.map(criterio =>
            criterio.id === id ? { ...criterio, [field]: value } : criterio
        );
        setCriterios(updatedCriterios);
    };

    const updateSlider = (id, field, value) => {
        const updatedSlider = sliderData.map(slider =>
            slider.id === id ? { ...slider, [field]: value } : slider
        );
        setSliderData(updatedSlider);
    };

    const updateCompare = (id, field, value) => {
        const updatedCompare = compareItems.map(compare =>
            compare.id === id ? { ...compare, [field]: value } : compare
        );
        setCompareItems(updatedCompare);
    };

    const updateOrgao = (id, field, value) => {
        const updatedOrgaos = orgaos.map(orgao =>
            orgao.id === id ? { ...orgao, [field]: value } : orgao
        );
        setOrgaos(updatedOrgaos);
    };

    const updateBlog = (id, field, value) => {
        setBlogs(prevBlogs =>
            prevBlogs.map(blog =>
                blog.id === id ? { ...blog, [field]: value } : blog
            )
        );
    };

    const handleContentChange = useCallback(
        debounce((id, value) => {
            updateBlog(id, 'content', value);
        }, 300), []);  // 300ms debounce delay

    const handleSave = async (type) => {
        const confirmSave = window.confirm("Você deseja confirmar a edição?");
    
        if (confirmSave) {
            try {
                switch (type) {
                    case 'criterios':
                        await Promise.all(criterios.map(criterio =>
                            axios.put(`http://localhost:3001/api/criterios/${criterio.id}`, {
                                title: criterio.title,
                                description: criterio.description
                            })
                        ));
                        break;
                    case 'slider':
                        await Promise.all(sliderData.map(slider =>
                            axios.put(`http://localhost:3001/api/slider/${slider.id}`, {
                                title: slider.title,
                                description: slider.description
                            })
                        ));
                        break;
                    case 'compare':
                        await Promise.all(compareItems.map(compare =>
                            axios.put(`http://localhost:3001/api/compare/${compare.id}`, {
                                brand: compare.brand,
                                product: compare.product,
                                reviewCount: compare.reviewCount,
                                features: compare.features
                            })
                        ));
                        break;
                    case 'orgaos':
                        await Promise.all(orgaos.map(orgao =>
                            axios.put(`http://localhost:3001/api/orgaos/${orgao.id}`, {
                                title: orgao.title,
                                description: orgao.description
                            })
                        ));
                        break;
                    case 'desconto':
                        if (descontoData) {
                            await axios.put(`http://localhost:3001/api/desconto/${descontoData.id}`, {
                                description: descontoData.description,
                                botao: descontoData.botao
                            });
                        }
                        break;
                    case 'blogs':
                        await Promise.all(blogs.map(blog =>
                            axios.put(`http://localhost:3001/api/blogs/${blog.id}`, {
                                slug: blog.slug,
                                title: blog.title,
                                description: blog.description,
                                content: blog.content,
                                image: blog.image,
                                link: blog.link
                            })
                        ));
                        break;
                    default:
                        console.error('Tipo desconhecido:', type);
                        return;
                }

                alert("Atualização realizada com sucesso!");
            } catch (error) {
                console.error("Erro ao salvar:", error);
                alert("Ocorreu um erro ao tentar atualizar.");
            }
        }
    };

    return (
        <>
            <header className="admin-header">
                <h1 className="admin-title">Admin Dashboard</h1>
                <button onClick={() => { localStorage.removeItem("authToken"); navigate("/login"); }} className="logout-button">Logout</button>
            </header>
            <div className="admin-layout">
                <aside className="admin-sidebar">
                    <ul className="admin-menu">
                        <li onClick={() => setSelectedSection('home')}>Home</li>
                        <li onClick={() => setSelectedSection('criterios')}>Critérios</li>
                        <li onClick={() => setSelectedSection('slider')}>Slider</li>
                        <li onClick={() => setSelectedSection('compare')}>Compare</li>
                        <li onClick={() => setSelectedSection('orgaos')}>Órgãos Regulamentadores</li>
                        <li onClick={() => setSelectedSection('desconto')}>Desconto</li>
                        <li onClick={() => setSelectedSection('blogs')}>Blogs</li>
                    </ul>
                </aside>
                <main className="admin-content">
                    {renderSection()}
                </main>
            </div>
        </>
    );
};

export default AdminPage;
