import React from 'react';
import './Orgaos.css';

function Orgaos(){
    return(
        <div className='orgaos-section'>
            <h2 className="info-text">Órgãos reguladores</h2>
            <p className='subinfo'>Conheça os órgãos que certificam o mercado de colchões</p>
            <div className='orgaos-list'>
                <div className='orgaos-itens'>
                    <h3 className='orgaos-tittle'>
                    <img src="https://bfbaby.com.br/up/icon-criterio.png" alt="Icone critério" className="orgao-icon" />
                    INER
                    </h3>
                    <p className='orgao-description'>
                    O Instituto Nacional de Estudos do Repouso (INER) foi criado em 1984 para regulamentar o mercado de colchões no Brasil. Ele realiza testes rigorosos nas espumas e molas usadas nos colchões, concedendo os selos Pró-Espuma e Pró-Molas às indústrias que atendem aos padrões exigidos. O INER é a principal entidade responsável pela fiscalização da qualidade dos colchões no país.
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Orgaos;
