import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Compare.css';

function Compare() {
    const [compareItems, setCompareItems] = useState([]);

    useEffect(() => {
        fetchCompareItems();
    }, []);

    const fetchCompareItems = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/compare');
            setCompareItems(response.data);
        } catch (error) {
            console.error("Erro ao buscar itens do Compare:", error);
        }
    };

    return (
        <div className="compare-section">
            <h2 className="info-text">Compare O Melhor Colchão</h2>
            <p className="subinfo">Veja a comparação entre os melhores colchões</p>
            <div className="compare-list">
                {compareItems.map(item => (
                    <div className="compare-item" key={item.id}>
                        <img 
                            src={item.image} 
                            alt={item.product} 
                            className='image' 
                        />
                        <div className='compare-content'>
                            <h3 className="compare-title">{item.brand}</h3>
                            <h4 className="compare-product">{item.product}</h4>

                            <ul className='features'>
                                {item.features.map((feature, index) => (
                                    <li key={index}>
                                        <img src="https://bfbaby.com.br/up/check.png" alt='Check'/>
                                        {`${feature.feature} ${feature.rating}`}
                                    </li>
                                ))}
                            </ul>
                        </div>
                        <div className='stars'>
                            <img src={item.rating} alt="Estrelas" className='rating-image' />
                            <span className='review-number'>{item.reviewCount}</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Compare;
