import React, { useEffect, useState } from "react";
import './Materia.css';
import Header from "../../components/Header/Header";
import BlogCarrossel from "../../components/BlogCarrossel/BlogCarrossel";
import Footer from "../../components/Footer/Footer";
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Materia = () => {
    const { slug } = useParams(); // Obtém o slug da URL
    const [materia, setMateria] = useState(null);

    useEffect(() => {
        // Busca a matéria correspondente pelo slug
        axios.get(`http://localhost:3001/api/blogs/${slug}`)
            .then(response => setMateria(response.data))
            .catch(error => console.error('Erro ao carregar a matéria:', error));
    }, [slug]);

    if (!materia) {
        return <p>Carregando...</p>;
    }

    return (
        <>
            <Header />
            <div className="blog-post">
                <h1 className="title">Matéria do blog</h1>
                <div className="image-placeholder" style={{ backgroundImage: `url(${materia.image})` }}></div>
                <h1 className="titulo-materia">{materia.title}</h1>
                <hr />
                <div className="content">
                    <p>{materia.content}</p>
                </div>
            </div>
            <BlogCarrossel />
            <Footer />
        </>
    );
}

export default Materia;
