import React from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './BlogsSection.css';

const BlogsSection = ({ blogs, updateBlog, handleSave }) => {
    const handleContentChange = (id, content) => {
        updateBlog(id, 'content', content);
    };

    return (
        <div className="blogs-section">
            <h2 className="section-title">Editar Blogs</h2>
            {blogs.map((blog, index) => (
                <div key={blog.id} className="blog-edit-section">
                    <h3 className="blog-item-label">Blog {index + 1}</h3>
                    <input
                        type="text"
                        value={blog.slug}
                        onChange={(e) => updateBlog(blog.id, 'slug', e.target.value)}
                        className="blog-edit-input"
                        placeholder="Slug"
                    />
                    <input
                        type="text"
                        value={blog.title}
                        onChange={(e) => updateBlog(blog.id, 'title', e.target.value)}
                        className="blog-edit-input"
                        placeholder="Título"
                    />
                    <textarea
                        value={blog.description}
                        onChange={(e) => updateBlog(blog.id, 'description', e.target.value)}
                        className="blog-edit-textarea"
                        placeholder="Descrição"
                    />
                    <ReactQuill 
                        value={blog.content}
                        onChange={(content) => handleContentChange(blog.id, content)}
                        placeholder="Escreva a matéria do blog aqui..."
                        className="blog-edit-quill"
                    />
                    <input
                        type="text"
                        value={blog.image}
                        onChange={(e) => updateBlog(blog.id, 'image', e.target.value)}
                        className="blog-edit-input"
                        placeholder="URL da Imagem"
                    />
                    <input
                        type="text"
                        value={blog.link}
                        onChange={(e) => updateBlog(blog.id, 'link', e.target.value)}
                        className="blog-edit-input"
                        placeholder="Link do Blog"
                    />
                </div>
            ))}
            <div className="button-group">
                <button onClick={() => handleSave('blogs')} className="save-button">Salvar</button>
            </div>
        </div>
    );
};

export default BlogsSection;
