import React from 'react';
import { Swiper, SwiperSlide } from "swiper/react";
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { Navigation, Pagination, A11y } from 'swiper/modules';

function Slider({ settings, children }) {
    return (
        <Swiper 
            modules={[Navigation, Pagination, A11y]} 
            {...settings}
            navigation={true} // Certificando que a navegação (setas) está habilitada
            pagination={{ clickable: true }} // Certificando que a paginação está habilitada
            spaceBetween={50} // Espaçamento entre slides
            slidesPerView={3} // Quantos slides visíveis
            grabCursor={true} // Cursor de "mão"
        >
            {children}
        </Swiper>
    );
}

export default Slider;
