import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './AdminPage.css';

const AdminPage = () => {
    const navigate = useNavigate();
    const [criterios, setCriterios] = useState([]);
    const [sliderData, setSliderData] = useState([]);
    const [compareItems, setCompareItems] = useState([]);
    const [descontoData, setDescontoData] = useState(null);
    const [selectedCriterio, setSelectedCriterio] = useState(null);
    const [selectedSlider, setSelectedSlider] = useState(null);
    const [selectedCompareItem, setSelectedCompareItem] = useState(null);
    const [originalCriterio, setOriginalCriterio] = useState(null);
    const [originalSlider, setOriginalSlider] = useState(null);
    const [originalCompareItem, setOriginalCompareItem] = useState(null);
    const [originalDescontoData, setOriginalDescontoData] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem("authToken");
        if (!token) {
            navigate("/login");
        } else {
            fetchCriterios();
            fetchSliderData();
            fetchCompareItems();
            fetchDescontoData();
        }
    }, [navigate]);

    const fetchCriterios = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/criterios');
            setCriterios(response.data);
        } catch (error) {
            console.error("Erro ao buscar critérios:", error);
        }
    };

    const fetchSliderData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/slider');
            setSliderData(response.data);
        } catch (error) {
            console.error("Erro ao buscar dados do slider:", error);
        }
    };

    const fetchCompareItems = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/compare');
            setCompareItems(response.data);
        } catch (error) {
            console.error("Erro ao buscar itens do Compare:", error);
        }
    };

    const fetchDescontoData = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/desconto');
            setDescontoData(response.data[0]);
            setOriginalDescontoData(response.data[0]);
        } catch (error) {
            console.error("Erro ao buscar dados do desconto:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("authToken");
        navigate("/login");
    };

    const handleCriterioChange = (e) => {
        const criterioId = parseInt(e.target.value);
        const criterio = criterios.find(c => c.id === criterioId);
        setSelectedCriterio({ ...criterio });
        setOriginalCriterio({ ...criterio });  // Salva o estado original
    };

    const handleSliderChange = (e) => {
        const sliderId = parseInt(e.target.value);
        const slider = sliderData.find(s => s.id === sliderId);
        setSelectedSlider({ ...slider });
        setOriginalSlider({ ...slider });  // Salva o estado original
    };

    const handleCompareChange = (e) => {
        const compareItemId = parseInt(e.target.value);
        const compareItem = compareItems.find(c => c.id === compareItemId);
        setSelectedCompareItem({ ...compareItem });
        setOriginalCompareItem({ ...compareItem });  // Salva o estado original
    };

    const handleFeatureChange = (index, field, value) => {
        const updatedFeatures = [...selectedCompareItem.features];
        updatedFeatures[index][field] = value;
        setSelectedCompareItem({ ...selectedCompareItem, features: updatedFeatures });
    };

    const handleCancel = (type) => {
        if (type === 'criterio') {
            setSelectedCriterio({ ...originalCriterio });
        } else if (type === 'slider') {
            setSelectedSlider({ ...originalSlider });
        } else if (type === 'compare') {
            setSelectedCompareItem({ ...originalCompareItem });
        } else if (type === 'desconto') {
            setDescontoData({ ...originalDescontoData });
        }
    };

    const handleSave = async (type) => {
        const confirmSave = window.confirm("Você deseja confirmar a edição?");
    
        if (confirmSave) {
            try {
                if (type === 'criterio' && selectedCriterio) {
                    await axios.put(`http://localhost:3001/api/criterios/${selectedCriterio.id}`, {
                        title: selectedCriterio.title,
                        description: selectedCriterio.description
                    });
                } else if (type === 'slider' && selectedSlider) {
                    await axios.put(`http://localhost:3001/api/slider/${selectedSlider.id}`, {
                        title: selectedSlider.title,
                        description: selectedSlider.description
                    });
                } else if (type === 'compare' && selectedCompareItem) {
                    await axios.put(`http://localhost:3001/api/compare/${selectedCompareItem.id}`, {
                        brand: selectedCompareItem.brand,
                        product: selectedCompareItem.product,
                        reviewCount: selectedCompareItem.reviewCount,
                        features: selectedCompareItem.features
                    });
                } else if (type === 'desconto' && descontoData) {
                    await axios.put(`http://localhost:3001/api/desconto/${descontoData.id}`, {
                        botao: descontoData.botao,
                        description: descontoData.description
                    });
                }

                alert("Atualização realizada com sucesso!");
                fetchCriterios();
                fetchSliderData();
                fetchCompareItems();
                fetchDescontoData();
            } catch (error) {
                console.error("Erro ao salvar:", error);
                alert("Ocorreu um erro ao tentar atualizar.");
            }
        } else {
            handleCancel(type);
        }
    };

    return (
        <>
            <header className="admin-header">
                <h1 className="admin-title">Admin Dashboard</h1>
                <button onClick={handleLogout} className="logout-button">Logout</button>
            </header>
            
            <main className="admin-container">
                <p className="admin-text">Selecione um critério, item do slider, item do compare ou conteúdo do desconto para editar.</p>

                <section className="criteria-section">
                    <h2 className="section-title">Editar Critérios</h2>
                    <select onChange={handleCriterioChange} className="select-input">
                        <option value="">Selecione um critério</option>
                        {criterios.map(criterio => (
                            <option key={criterio.id} value={criterio.id}>{criterio.title}</option>
                        ))}
                    </select>

                    {selectedCriterio && (
                        <div className="edit-section">
                            <input
                                type="text"
                                name="title"
                                value={selectedCriterio.title}
                                onChange={(e) => setSelectedCriterio({ ...selectedCriterio, title: e.target.value })}
                                className="edit-input"
                                placeholder="Título"
                            />
                            <textarea
                                name="description"
                                value={selectedCriterio.description}
                                onChange={(e) => setSelectedCriterio({ ...selectedCriterio, description: e.target.value })}
                                className="edit-textarea"
                                placeholder="Descrição"
                            />
                            <div className="button-group">
                                <button onClick={() => handleSave('criterio')} className="save-button">Salvar</button>
                            </div>
                        </div>
                    )}
                </section>

                <section className="slider-section">
                    <h2 className="section-title">Editar Slider</h2>
                    <select onChange={handleSliderChange} className="select-input">
                        <option value="">Selecione um item do slider</option>
                        {sliderData.map(slider => (
                            <option key={slider.id} value={slider.id}>{slider.title}</option>
                        ))}
                    </select>

                    {selectedSlider && (
                        <div className="edit-section">
                            <input
                                type="text"
                                name="title"
                                value={selectedSlider.title}
                                onChange={(e) => setSelectedSlider({ ...selectedSlider, title: e.target.value })}
                                className="edit-input"
                                placeholder="Título"
                            />
                            <textarea
                                name="description"
                                value={selectedSlider.description}
                                onChange={(e) => setSelectedSlider({ ...selectedSlider, description: e.target.value })}
                                className="edit-textarea"
                                placeholder="Descrição"
                            />
                            <div className="button-group">
                                <button onClick={() => handleSave('slider')} className="save-button">Salvar</button>
                            </div>
                        </div>
                    )}
                </section>

                <section className="compare-section">
                    <h2 className="section-title">Editar Itens do Compare</h2>
                    <select onChange={handleCompareChange} className="select-input">
                        <option value="">Selecione um Colchão do compare</option>
                        {compareItems.map(compare => (
                            <option key={compare.id} value={compare.id}>{compare.product}</option>
                        ))}
                    </select>

                    {selectedCompareItem && (
                        <div className="edit-section">
                            <input
                                type="text"
                                name="brand"
                                value={selectedCompareItem.brand}
                                onChange={(e) => setSelectedCompareItem({ ...selectedCompareItem, brand: e.target.value })}
                                className="edit-input"
                                placeholder="Marca"
                            />
                            <input
                                type="text"
                                name="product"
                                value={selectedCompareItem.product}
                                onChange={(e) => setSelectedCompareItem({ ...selectedCompareItem, product: e.target.value })}
                                className="edit-input"
                                placeholder="Produto"
                            />
                            <input
                                type="text"
                                name="reviewCount"
                                value={selectedCompareItem.reviewCount}
                                onChange={(e) => setSelectedCompareItem({ ...selectedCompareItem, reviewCount: e.target.value })}
                                className="edit-input"
                                placeholder="Contagem de Avaliações"
                            />
                            <h3>Editar Features:</h3>
                            {selectedCompareItem.features.map((feature, index) => (
                                <div key={index} className="feature-edit">
                                    <input
                                        type="text"
                                        value={feature.feature}
                                        onChange={(e) => handleFeatureChange(index, 'feature', e.target.value)}
                                        className="edit-input"
                                        placeholder="Feature"
                                    />
                                    <input
                                        type="text"
                                        value={feature.rating}
                                        onChange={(e) => handleFeatureChange(index, 'rating', e.target.value)}
                                        className="edit-input"
                                        placeholder="Rating"
                                    />
                                </div>
                            ))}
                            <div className="button-group">
                                <button onClick={() => handleSave('compare')} className="save-button">Salvar</button>
                            </div>
                        </div>
                    )}
                </section>

                <section className="desconto-section">
                    <h2 className="section-title">Editar Desconto</h2>
                    {descontoData && (
                        <div className="edit-section">
                            <textarea
                                name="description"
                                value={descontoData.description}
                                onChange={(e) => setDescontoData({ ...descontoData, description: e.target.value })}
                                className="edit-textarea"
                                placeholder="Descrição do Desconto"
                            />
                            <input
                                type="text"
                                name="botao"
                                value={descontoData.botao}
                                onChange={(e) => setDescontoData({ ...descontoData, botao: e.target.value })}
                                className="edit-input"
                                placeholder="Texto do Botão"
                            />
                            <div className="button-group">
                                <button onClick={() => handleSave('desconto')} className="save-button">Salvar</button>
                            </div>
                        </div>
                    )}
                </section>
            </main>
        </>
    );
};

export default AdminPage;
