import React from "react";
import './BlogCarrossel.css';
import Slider from "react-slick";
import { Link } from 'react-router-dom';

function BlogCarrossel({ blogs }) {
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3.65,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return (
        <>
            <h1 className="BlogTitle">Blog</h1>

            <div className="slider-blog">
                <Slider {...settings} className="custom-slider-blog">
                    {blogs.map(blog => (
                        <div key={blog.id} className="custom-card-blog">
                            <div className="card-image-blog" style={{ backgroundImage: `url(${blog.image})` }}></div>
                            <div className="card-content-blog">
                                <h2>{blog.title}</h2>
                                <p>{blog.description.substring(0, 100)}...</p>
                                <Link to={`/materia/${blog.id}`}>
                                    <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="red-more" />
                                </Link>
                            </div>
                        </div>
                    ))}
                </Slider>
            </div>
        </>
    );
};

export default BlogCarrossel;
