import React from "react";
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import Header from './components/Header/Header';
import TitleSection from './components/TitleSection/TitleSection';
import CriteriaSection from './components/CriteriaSection/CriteriaSection';
import AdminPage from './Pages/AdminPage/AdminPage';
import LoginPage from './components/LoginPage/LoginPage';
import ProtectedRoute from "./components/ProtectedRoute";
import Slider from "./components/Slider/Slider";
import Compare from './components/Compare/Compare';
import Orgaos from './components/Orgaos/Orgaos';
import Desconto from './components/Desconto/Desconto';
import BlogCarrossel from './components/BlogCarrossel/BlogCarrossel'
import Faq from './components/faq/faq';
import Footer from './components/Footer/Footer';
import Materia from "./Pages/Materia/Materia";


const App = () => {
    const location = useLocation();

    return (
        <div>
            {location.pathname !== "/admin" && location.pathname !== "/login" && <Header />}
            {location.pathname !== "/admin" && location.pathname !== "/login" && <TitleSection />}
            {location.pathname !== "/admin" && location.pathname !== "/login" && <CriteriaSection />}
            {location.pathname !== "/admin" && location.pathname !== "/login" && <Slider /> }
            {location.pathname !== "/admin" && location.pathname !== "/login" && <Orgaos /> }
            {location.pathname !== "/admin" && location.pathname !== "/login" && <Compare /> }
            {location.pathname !== "/admin" && location.pathname !== "/login" && <Desconto /> }
            {location.pathname !== "/admin" && location.pathname !== "/login" && <BlogCarrossel /> }
            {location.pathname !== "/admin" && location.pathname !== "/login" && <Faq /> }
            {location.pathname !== "/admin" && location.pathname !== "/login" && <Footer /> }

            <Routes>
    <Route path="/admin" element={
        <ProtectedRoute>
            <AdminPage />
        </ProtectedRoute>
    } />
    <Route path="/login" element={<LoginPage />} />
    <Route path="/materia" element={<Materia />} />  {/* Mantenha apenas as rotas que deseja */}
</Routes>

        </div>
    );
}

const AppWrapper = () => (
    <Router>
        <App />
    </Router>
);

export default AppWrapper;
