import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import './Materia.css';
import Header from "../../components/Header/Header";
import BlogCarrossel from "../../components/BlogCarrossel/BlogCarrossel";
import Footer from "../../components/Footer/Footer";

const Materia = () => {
  const { slug } = useParams();  // Pegando o slug da URL
  const [materia, setMateria] = useState(null);

  useEffect(() => {
    // Requisição para buscar a matéria com base no slug
    const fetchMateria = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/blogs/${slug}`);
        setMateria(response.data);
      } catch (error) {
        console.error("Erro ao carregar a matéria:", error);
      }
    };

    fetchMateria();
  }, [slug]);

  if (!materia) {
    return <div>Carregando...</div>;
  }

  return (
    <>
      <Header />
      <div className="blog-post">
        <h1 className="title">{materia.title}</h1>
        <div className="image-placeholder" style={{ backgroundImage: `url(${materia.image})` }}></div>
        <h1 className="titulo-materia">{materia.title}</h1>
        <hr />
        <div className="content">
          <p>{materia.description}</p>
          <div dangerouslySetInnerHTML={{ __html: materia.content }} />
        </div>
      </div>
      <BlogCarrossel />
      <Footer />
    </>
  );
};

export default Materia;
