import React from "react";
import './BlogCarrossel.css';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

function PrevArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-prev-arrow`}
            style={{ ...style, display: "block" }}
            onClick={onClick}
        />
    );
}

function NextArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-next-arrow`}
            style={{ ...style, display: "block" }}
            onClick={onClick}
        />
    );
}

function BlogCarrossel(){
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 2,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return(
    <>
        <h1 className="BlogTitle">Blog</h1>

        <div className="slider-blog">
            <Slider {...settings} className="custom-slider-blog">
                <div className="custom-card-blog">
                    <div className="card-image-blog" ></div>
                    <div className="card-content-blog">
                        <h2>O que é um colchão certificado e por que é importante pensar nisso na hora de dormir?</h2>
                        <p>O que é um colchão certificado e por que é importante pensar nisso na hora de dormir?</p>
                    </div>
                </div>
            </Slider>
        </div>
    </>
    );
};

export default BlogCarrossel;
