import React from 'react';
import './Header.css';

function Header() {
  const handleScroll = (event, targetId) => {
    event.preventDefault();
    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      targetElement.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <header className="header">
      <p className="logo-text">O Melhor Colchão</p>
      <nav className="nav-links">
        <a href="#Compare" onClick={(e) => handleScroll(e, 'Compare')}>Compare</a>
        <a href="#blog" onClick={(e) => handleScroll(e, 'blog')}>Blog</a>
        <a href="#faq" onClick={(e) => handleScroll(e, 'faq')}>Perguntas Frequentes</a>
      </nav>
    </header>
  );
}

export default Header;
