import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./CustomSlider.css";

function PrevArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-prev-arrow`}
            style={{ ...style, display: "block" }}
            onClick={onClick}
        />
    );
}

function NextArrow(props) {
    const { className, style, onClick } = props;
    return (
        <div
            className={`${className} custom-next-arrow`}
            style={{ ...style, display: "block" }}
            onClick={onClick}
        />
    );
}

function CustomSlider() {
    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    const data = [
        {
            tittle: `Lorem Ipsum`,
            image: `https://via.placeholder.com/150`, // Exemplo de URL de imagem
            description: `Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis quis libero tempor mauris pulvinar pharetra eu a nunc. Morbi condimentum ex et arcu dignissim, eu elementum sem placerat.`,
        },
        {
            tittle: `Lorem Ipsum`,
            image: `https://via.placeholder.com/150`,
            description: `Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis quis libero tempor mauris pulvinar pharetra eu a nunc. Morbi condimentum ex et arcu dignissim, eu elementum sem placerat.`,
        },
        // Adicione mais cards conforme necessário
    ];

    return (
        <div className="slider-container">
            <Slider {...settings}>
                {data.map((d) => (
                    <div className="custom-card" key={d.tittle}>
                        <div className="card-image-placeholder">
                            <img src={d.image} alt={d.tittle} className="card-image" />
                        </div>
                        <div className="card-text">
                            <p className="card-title">{d.tittle}</p>
                            <p>{d.description}</p>
                        </div>
                    </div>
                ))}
            </Slider>
        </div>
    );
}

export default CustomSlider;
