import React, { useState, useEffect } from "react";
import './Blog.css';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Blog(){
    const [blog, setBlog] = useState({});

    useEffect(() => {
        axios.get('http://localhost:3001/api/blogs')
            .then(response => {
                const blogs = response.data;
                const latestBlog = blogs.reduce((prev, current) => (prev.id > current.id) ? prev : current);
                setBlog(latestBlog);
            })
            .catch(error => console.error("Erro ao buscar blogs:", error));
    }, []);

    return(
        <>
            <Header />
            <div className="blog-container">
                <a href="/">
                    <img src="https://bfbaby.com.br/up/voltar.png" alt="voltar" className="back" />
                </a>
                <h1 className="title-blog">Blog</h1>
                <div className="blog-content">
                    <div className="images-placeholder" style={{ backgroundImage: `url(${blog.image2 || ''})` }}></div>
                    <div className="text-content">
                        <h2>{blog.title || 'Título do Blog'}</h2>
                        <p>{blog.description || 'Descrição do Blog'}</p>
                        <Link to={`/materia/${blog.slug}`}>
                            <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="leiaMais" />
                        </Link>
                    </div>
                </div>


                <div key={blog.slug} className="custom-card-blog">
                    <div className="card-image-blog" style={{ backgroundImage: `url(${blog.image})` }}></div>
                    <div className="card-content-blog">
                        <h2>{blog.title}</h2>
                        <p>{blog.description}</p>
                        <Link to={`/materia/${blog.slug}`}>
                                <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="red-more"/>
                            </Link>
                        </div>
                    </div>
                

            </div>
            <Footer />
        </>
    );
}

export default Blog;
