import React, { useState, useEffect } from "react";
import axios from 'axios';
import './BlogCarrossel.css';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Link } from 'react-router-dom';

function BlogCarrossel(){
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3001/api/blogs')
            .then(response => {
                setBlogs(response.data);
            })
            .catch(error => {
                console.error("Erro ao buscar blogs:", error);
            });
    }, []);

    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3.65,
        slidesToScroll: 1,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return(
    <>
        <h1 className="BlogTitle">Blog</h1>
        <div className="slider-blog">
            <Slider {...settings} className="custom-slider-blog">
                {blogs.map(blog => (
                    <div className="custom-card-blog" key={blog.id}>
                        <div className="card-image-blog" style={{ backgroundImage: `url(${blog.image})` }}></div>
                        <div className="card-content-blog">
                            <h2>{blog.title}</h2>
                            <p>{blog.description}</p>
                            <Link to={blog.link}>
                                <img src="https://bfbaby.com.br/up/leia_mais.png" alt="Leia mais" className="red-more" />
                            </Link>
                        </div>
                    </div>
                ))}
            </Slider>
        </div>
    </>
    );
}
